"use client";

import React, { forwardRef, useRef } from "react";

import { cn } from "@/lib/utils";
import { AnimatedBeam } from "@/components/ui/animated-beam";
import {ShieldCheckIcon} from "lucide-react";

const Circle = forwardRef<
  HTMLDivElement,
  { className?: string; children?: React.ReactNode }
>(({ className, children }, ref) => {
  return (
    <div
      ref={ref}
      className={cn(
        "z-10 flex size-12 items-center justify-center rounded-full border-2 bg-white p-3 shadow-[0_0_20px_-12px_rgba(0,0,0,0.8)]",
        className,
      )}
    >
      {children}
    </div>
  );
});

Circle.displayName = "Circle";

export function AnimatedBeamMultipleOutput({
  className,
}: {
  className?: string;
}) {
  const containerRef = useRef<HTMLDivElement>(null);
  // const div1Ref = useRef<HTMLDivElement>(null);
  const div2Ref = useRef<HTMLDivElement>(null);
  const div3Ref = useRef<HTMLDivElement>(null);
  const div4Ref = useRef<HTMLDivElement>(null);
  const div5Ref = useRef<HTMLDivElement>(null);
  const div6Ref = useRef<HTMLDivElement>(null);
  const div7Ref = useRef<HTMLDivElement>(null);

  return (
    <div
      className={cn(
        "relative flex h-[300px] md:h-[500px] w-full items-center justify-center md:overflow-hidden md:rounded-lg md:border bg-background md:p-10 md:shadow-xl",
        className,
      )}
      ref={containerRef}
    >
      {/*<p className="absolute top-10">*/}
      {/*  Ваш Сервер*/}
      {/*</p>*/}
      <div className="flex size-full max-w-lg flex-row items-stretch justify-between gap-10">
        <div className="flex flex-col justify-center">
          <Circle ref={div7Ref}>
            <Icons.user />
          </Circle>
        </div>
        <div className="flex flex-col justify-center">
          <Circle ref={div6Ref} className="size-30 flex flex-col md:flex-row gap-2 md:gap-1">
            <ShieldCheckIcon className="stroke-[1.3px]" />
            <span className="text-center">
              Celestial Cloud
            </span>
          </Circle>
        </div>
        <div className="flex flex-col justify-center gap-2">
          <Circle ref={div2Ref}>
            <Icons.claude />
          </Circle>
          <Circle ref={div3Ref}>
            <Icons.openai />
          </Circle>
          <Circle ref={div4Ref}>
            <Icons.deepseek />
          </Circle>
        </div>
      </div>

      {/* AnimatedBeams */}
      {/*<AnimatedBeam*/}
      {/*  containerRef={containerRef}*/}
      {/*  fromRef={div1Ref}*/}
      {/*  toRef={div6Ref}*/}
      {/*  duration={3}*/}
      {/*/>*/}
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div2Ref}
        toRef={div6Ref}
        duration={3}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div3Ref}
        toRef={div6Ref}
        duration={3}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div4Ref}
        toRef={div6Ref}
        duration={3}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div5Ref}
        toRef={div6Ref}
        duration={3}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div6Ref}
        toRef={div7Ref}
        duration={3}
      />
    </div>
  );
}

